// Procesar respuesta de la API de clima
const data = $input.first().json;
const current = data.current;

return {
  json: {
    ciudad: 'Madrid',
    temperatura: current.temperature_2m + '°C',
    codigo_clima: current.weather_code,
    velocidad_viento: current.wind_speed_10m + ' km/h',
    zona_horaria: data.timezone,
    actualizado: current.time,
    condicion: mapearClima(current.weather_code)
  }
};

function mapearClima(codigo) {
  const clima = {
    0: 'Despejado',
    1: 'Parcialmente nublado',
    2: 'Nublado',
    3: 'Muy nublado',
    45: 'Niebla',
    48: 'Niebla escarchada',
    51: 'Llovizna ligera',
    61: 'Lluvia ligera',
    71: 'Nieve ligera',
    80: 'Lluvia moderada',
    95: 'Tormenta'
  };
  return clima[codigo] || 'Desconocido';
}
